public class SwitchRepository\n{\n    public Dictionary<Switches, bool> GetSwitchesForClient(int clientId)\n    {\n        // Retrieve the switches from the database\n        var switches = new Dictionary<Switches, bool>();\n        foreach (var switchRow in db.ClientSwitches.Where(cs => cs.ClientId == clientId))\n        {\n            switches.Add((Switches)Enum.Parse(typeof(Switches), switchRow.SwitchName), switchRow.SwitchValue);\n        }\n        return switches;\n    }\n}