using System.Data;\nusing System.Data.OracleClient;\n\n// Create a new DataSet\nDataSet dataSet = new DataSet();\n\n// Create a connection string for the first schema\nstring connectionString1 = "Data Source=ORCL;User Id=myuser;Password=mypassword;";\n\n// Create a connection string for the second schema\nstring connectionString2 = "Data Source=ORCL;User Id=myuser;Password=mypassword;";\n\n// Create an OracleConnection object for the first schema\nOracleConnection connection1 = new OracleConnection(connectionString1);\n\n// Create an OracleConnection object for the second schema\nOracleConnection connection2 = new OracleConnection(connectionString2);\n\n// Create a DataAdapter for the first schema\nOracleDataAdapter dataAdapter1 = new OracleDataAdapter("SELECT * FROM mytable", connection1);\n\n// Create a DataAdapter for the second schema\nOracleDataAdapter dataAdapter2 = new OracleDataAdapter("SELECT * FROM mytable", connection2);\n\n// Fill the DataSet with the data from the first schema\ndataAdapter1.Fill(dataSet);\n\n// Or fill the DataSet with the data from the second schema\ndataAdapter2.Fill(dataSet);