using Fiddler;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        var fiddler = new FiddlerApplication();\n\n        // Start the proxy server\n        fiddler.Startup(8888, true, true);\n\n        // Use the proxy server to make requests\n        var client = new HttpClient();\n        client.DefaultRequestHeaders.ProxyAuthorization = new AuthenticationHeaderValue("Basic", Convert.ToBase64String(Encoding.ASCII.GetBytes("username:password")));\n\n        var response = await client.GetAsync("https://example.com");\n\n        Console.WriteLine($"Response: {response.StatusCode}");\n\n        // Shutdown the proxy server\n        fiddler.Shutdown();\n    }\n}