using WinPcap;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        // Open the default network interface\n        var pcap = new WinPcap("rpcap://\\Device\\NPF_{...}");\n\n        // Start capturing packets\n        pcap.StartCapture();\n\n        while (true)\n        {\n            // Read a packet from the capture\n            var packet = pcap.GetNextPacket();\n\n            // Parse the packet as an HTTP request or response\n            if (packet.Protocol == Protocol.Tcp)\n            {\n                var tcp = packet.Tcp;\n                var data = tcp.Payload;\n\n                // Parse the HTTP request or response\n                if (data.StartsWith("GET") || data.StartsWith("POST"))\n                {\n                    Console.WriteLine($"Request: {data}");\n                }\n                else if (data.StartsWith("HTTP/1.1"))\n                {\n                    Console.WriteLine($"Response: {data}");\n                }\n            }\n        }\n    }\n}