protected void Application_Error(object sender, EventArgs e)\n{\n    var exception = Server.GetLastError();\n    if (exception is HttpException httpException && httpException.Message == "The client disconnected.")\n    {\n        // The client disconnected, ignore this exception\n        Server.ClearError();\n        return;\n    }\n\n    // Log and email other exceptions\n    // ...\n}