// Set session value with expiration\nSession["sessionval"] = myvariable;\nSession["sessioncreated"] = DateTime.Now;\n\n// When retrieving the value, check if it's expired\nif (Session["sessioncreated"] != null)\n{\n    DateTime created = (DateTime)Session["sessioncreated"];\n    if ((DateTime.Now - created).TotalMinutes > 5)\n    {\n        // Expired, remove the values\n        Session.Remove("sessionval");\n        Session.Remove("sessioncreated");\n    }\n    else\n    {\n        // Not expired, use the value\n        myvariable = Session["sessionval"];\n    }\n}