private void CopyGraphics()\n{\n    using (Graphics srcPic = pnl.CreateGraphics())\n    {\n        using (Bitmap srcBmp = new Bitmap(pnl.Width, pnl.Height, srcPic))\n        {\n            using (Graphics srcMem = Graphics.FromImage(srcBmp))\n            {\n                srcMem.DrawImage(srcBmp, 0, 0);\n            }\n            pnlDraw.BackgroundImage = srcBmp;\n        }\n    }\n}