public class ExpiringSession\n{\n    private string _key;\n    private object _value;\n    private DateTime _expires;\n\n    public ExpiringSession(string key, object value, TimeSpan expiration)\n    {\n        _key = key;\n        _value = value;\n        _expires = DateTime.Now + expiration;\n    }\n\n    public object GetValue()\n    {\n        if (DateTime.Now > _expires)\n        {\n            // Expired, remove the value\n            Session.Remove(_key);\n            return null;\n        }\n        return _value;\n    }\n}