private void OnMouseMove(object sender, MouseEventArgs e)\n{\n    Point mousePosition = e.GetPosition(this);\n    HitTestResult result = VisualTreeHelper.HitTest(this, mousePosition);\n\n    if (result != null)\n    {\n        var hitTestResults = new List<DependencyObject>();\n        while (result != null)\n        {\n            hitTestResults.Add(result.VisualHit);\n            result = VisualTreeHelper.HitTest(this, mousePosition, result.VisualHit);\n        }\n        // hitTestResults now contains the elements under the mouse\n    }\n}