using System.IO;\n\n// In your HttpModule's Begin_Request event handler\npublic void BeginRequest(object sender, EventArgs e)\n{\n    HttpApplication app = sender as HttpApplication;\n    string url = app.Request.Url.AbsolutePath;\n\n    string fileName = Path.GetFileName(url);\n    string fileExtension = Path.GetExtension(fileName);\n\n    // Check if the URL ends with a file\n    if (!string.IsNullOrEmpty(fileName) && !string.IsNullOrEmpty(fileExtension))\n    {\n        // You can now check the file extension\n        if (fileExtension.Equals(".css", StringComparison.OrdinalIgnoreCase) ||\n            fileExtension.Equals(".js", StringComparison.OrdinalIgnoreCase))\n        {\n            // Handle CSS or JS file request\n        }\n    }\n}