using System.DirectoryServices.Protocols;\n\n// Connect to the ADAM instance\nLdapConnection connection = new LdapConnection("localhost:389");\nconnection.Credential = new NetworkCredential("johndoe", "password");\nconnection.Bind();\n\n// Search for the user\nSearchRequest request = new SearchRequest("CN=John Doe,CN=Users,DC=example,DC=test", "(objectClass=user)", SearchScope.Subtree);\nSearchResponse response = (SearchResponse)connection.SendRequest(request);\n\n// Validate the user\nif (response.Entries.Count > 0)\n{\n    Console.WriteLine("User found!");\n}