private void CompareValues(DataRow row, Control control)\n{\n    foreach (PropertyInfo property in control.GetType().GetProperties())\n    {\n        if (row.Table.Columns.Contains(property.Name))\n        {\n            object controlValue = property.GetValue(control, null);\n            object rowValue = row[property.Name];\n\n            if (!object.Equals(controlValue, rowValue))\n            {\n                row[property.Name] = controlValue;\n                _whatChanged.Add(property.Name);\n            }\n        }\n    }\n}