using System.Windows.Forms;\n\n// Get the current screen resolution and DPI\nScreen screen = Screen.PrimaryScreen;\nint dpi = screen.PrimaryScreen.DeviceDpi;\n\n// Define your image sizes and corresponding DPI ranges\nint smallImageSize = 16;\nint largeImageSize = 32;\nint lowDpiThreshold = 96; // adjust this value as needed\n\n// Choose the image size based on the DPI\nif (dpi <= lowDpiThreshold)\n{\n    // Display small image (16x16)\n    image.Width = smallImageSize;\n    image.Height = smallImageSize;\n}\nelse\n{\n    // Display large image (32x32)\n    image.Width = largeImageSize;\n    image.Height = largeImageSize;\n}