using System.Collections.Generic;\nusing System.Linq;\nusing System.Windows.Controls;\n\npublic class UniqueNameValidationRule : ValidationRule\n{\n    public override ValidationResult Validate(object value, CultureInfo cultureInfo)\n    {\n        var name = value as string;\n        var collection = this.Collection;\n\n        if (collection != null && name != null)\n        {\n            if (collection.OfType<object>().Any(item => item.GetType().GetProperty("Name")?.GetValue(item) as string == name))\n            {\n                return new ValidationResult(false, "Name is not unique.");\n            }\n        }\n\n        return ValidationResult.ValidResult;\n    }\n\n    public IEnumerable<object> Collection { get; set; }\n}