using System;\nusing System.Xml;\n\nnamespace SharePointListsWS\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Create a new instance of the Lists web service\n            ListsService.Lists lists = new ListsService.Lists();\n            lists.Url = "http://yoursharepointsite/_vti_bin/Lists.asmx";\n            lists.Credentials = System.Net.CredentialCache.DefaultCredentials;\n\n            // Define the CAML query to insert a new item\n            string camlQuery = @"\n                <Batch OnError=""Continue"" ListVersion=""1"">\n                    <Method ID=""1"" Cmd=""New"">\n                        <Field Name=""Title"">New Item Title</Field>\n                        <Field Name=""TextField1"">Text Field 1 Value</Field>\n                        <Field Name=""TextField2"">Text Field 2 Value</Field>\n                        <Field Name=""ChoiceField"">Choice 1</Field>\n                    </Method>\n                </Batch>";\n\n            // Specify the list name and the CAML query\n            XmlDocument xmlDoc = new XmlDocument();\n            XmlElement query = xmlDoc.CreateElement("Query");\n            query.InnerXml = camlQuery;\n\n            // Call the UpdateListItems method to insert the new item\n            XmlNode response = lists.UpdateListItems("YourListName", query);\n\n            // Check the response to ensure the item was inserted successfully\n            if (response.InnerText.Contains("Error"))\n            {\n                Console.WriteLine("Error inserting new item: " + response.InnerText);\n            }\n            else\n            {\n                Console.WriteLine("New item inserted successfully");\n            }\n        }\n    }\n}