using CsvHelper;\nusing CsvHelper.Configuration;\n\n// Create a CsvReader instance\nusing (CsvReader reader = new CsvReader(new StringReader("1,\"Your simple algorithm, it fails\",True")))\n{\n    // Read records\n    while (reader.Read())\n    {\n        string field1 = reader.GetField(0);\n        string field2 = reader.GetField(1);\n        string field3 = reader.GetField(2);\n\n        Console.WriteLine(field1);\n        Console.WriteLine(field2);\n        Console.WriteLine(field3);\n    }\n}