// Create a BindingSource for each ComboBox\nBindingSource titleSource = new BindingSource();\nBindingSource typeSource = new BindingSource();\nBindingSource nameSource = new BindingSource();\n\n// Set the DataSource of each ComboBox to the corresponding BindingSource\nTitle_Combo.DataSource = titleSource;\nType_Combo.DataSource = typeSource;\nName_Combo.DataSource = nameSource;\n\n// Load the data into the BindingSources\ntitleSource.DataSource = ListContaining.GroupBy(item => item.Title).Where(item => !item.Key.Equals(string.Empty)).ToList();\ntypeSource.DataSource = ListContaining.GroupBy(item => item.Type).Where(item => !item.Key.Equals(string.Empty)).ToList();\nnameSource.DataSource = ListContaining.GroupBy(item => item.Name).Where(item => !item.Key.Equals(string.Empty)).ToList();\n\n// Set the DisplayMember of each ComboBox\nTitle_Combo.DisplayMember = "Key";\nType_Combo.DisplayMember = "Key";\nName_Combo.DisplayMember = "Key";\n\n// Update the data sources on index change\nTitle_Combo.SelectedIndexChanged += (sender, e) => {\n    ListContaining = ListFiles.Where(item => item.Title.Equals(Title_Combo.SelectedItem.ToString())).ToList();\n    typeSource.DataSource = ListContaining.GroupBy(item => item.Type).Where(item => !item.Key.Equals(string.Empty)).ToList();\n    nameSource.DataSource = ListContaining.GroupBy(item => item.Name).Where(item => !item.Key.Equals(string.Empty)).ToList();\n};\n\nType_Combo.SelectedIndexChanged += (sender, e) => {\n    ListContaining = ListFiles.Where(item => item.Type.Equals(Type_Combo.SelectedItem.ToString())).ToList();\n    titleSource.DataSource = ListContaining.GroupBy(item => item.Title).Where(item => !item.Key.Equals(string.Empty)).ToList();\n    nameSource.DataSource = ListContaining.GroupBy(item => item.Name).Where(item => !item.Key.Equals(string.Empty)).ToList();\n};\n\nName_Combo.SelectedIndexChanged += (sender, e) => {\n    ListContaining = ListFiles.Where(item => item.Name.Equals(Name_Combo.SelectedItem.ToString())).ToList();\n    titleSource.DataSource = ListContaining.GroupBy(item => item.Title).Where(item => !item.Key.Equals(string.Empty)).ToList();\n    typeSource.DataSource = ListContaining.GroupBy(item => item.Type).Where(item => !item.Key.Equals(string.Empty)).ToList();\n};