using System.Data.SqlClient;\nusing System.Threading.Tasks;\n\n// Assume you have a web service method that returns data from two databases\npublic async Task<DataResult> GetDataAsync()\n{\n    // Create two separate connections\n    using (var conn1 = new SqlConnection("Master DB Connection String"))\n    using (var conn2 = new SqlConnection("Extensions DB Connection String"))\n    {\n        // Open connections asynchronously\n        await Task.WhenAll(conn1.OpenAsync(), conn2.OpenAsync());\n\n        // Execute queries asynchronously\n        var query1 = conn1.CreateCommand();\n        query1.CommandText = "SELECT * FROM MasterTable";\n        var query2 = conn2.CreateCommand();\n        query2.CommandText = "SELECT * FROM ExtensionsTable";\n\n        var results = await Task.WhenAll(query1.ExecuteReaderAsync(), query2.ExecuteReaderAsync());\n\n        // Combine data from both queries\n        var dataResult = new DataResult();\n        // ...\n\n        return dataResult;\n    }\n}