using System;\nusing System.Windows.Forms;\nusing AxPDFVIEWLib;\n\nnamespace SecurePdfViewer\n{\n    public class PdfViewerForm : Form\n    {\n        private AxPDFView _pdfView;\n\n        public PdfViewerForm(string pdfPath)\n        {\n            // Initialize the Foxit PDF View control\n            _pdfView = new AxPDFView();\n            _pdfView.Dock = DockStyle.Fill;\n            this.Controls.Add(_pdfView);\n\n            // Open the PDF securely\n            _pdfView.OpenFile(pdfPath, "");\n\n            // Enable annotations\n            _pdfView.AnnotationMode = PDFAnnotMode.PDFAnnotModeCreate;\n        }\n\n        private void PdfViewerForm_FormClosing(object sender, FormClosingEventArgs e)\n        {\n            // Clean up resources\n            _pdfView.Dispose();\n        }\n    }\n}