[AcceptVerbs(HttpVerbs.Post)]\npublic ActionResult GenericPostHandler(NewAccountInfo info)\n{\n    try\n    {\n        var routeData = HttpContext.Request.RequestContext.RouteData.Values;\n        string currentView = $"{routeData["controller"]}/{routeData["action"]}";\n        Persist();\n\n        return RedirectToAction(StepManager.GetNextStep(currentView));\n    }\n    catch\n    {\n        return View();\n    }\n}