protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        DataRow row = ((DataRowView)e.Row.DataItem).Row;\n\n        int aCount = Convert.ToInt32(row["ACount"]);\n        int bCount = Convert.ToInt32(row["BCount"]);\n        int dCount = Convert.ToInt32(row["DCount"]);\n\n        Label lblACount = (Label)e.Row.FindControl("lblACount");\n        Label lblBCount = (Label)e.Row.FindControl("lblBCount");\n        Label lblDCount = (Label)e.Row.FindControl("lblDCount");\n\n        if (aCount < 0)\n            lblACount.Text = "S";\n        else if (aCount > 0)\n            lblACount.Text = "D";\n\n        if (bCount < 0)\n            lblBCount.Text = "S";\n        else if (bCount > 0)\n            lblBCount.Text = "D";\n\n        if (dCount < 0)\n            lblDCount.Text = "S";\n        else if (dCount > 0)\n            lblDCount.Text = "D";\n    }\n}