public static MvcHtmlString ValidationImage(this HtmlHelper htmlHelper, string modelName)\n{\n    var validationMessage = htmlHelper.ValidationMessage(modelName);\n    if (validationMessage != null && !string.IsNullOrEmpty(validationMessage.ToString()))\n    {\n        var errorText = HtmlRemoval.StripTags(validationMessage.ToString());\n        var imgTag = new TagBuilder("img");\n        imgTag.MergeAttribute("src", "Error.gif");\n        imgTag.MergeAttribute("alt", "Error");\n        imgTag.MergeAttribute("title", errorText);\n        return MvcHtmlString.Create(imgTag.ToString());\n    }\n    return MvcHtmlString.Empty;\n}