// Create a search filter to find the user object\nSearchRequest searchRequest = new SearchRequest(\n    "dc=your-domain,dc=com", // base DN\n    "(&(objectClass=user)(uid=DaffyD))", // filter\n    SearchScope.Subtree, // search scope\n    new string[] { "parent" } // attributes to retrieve\n);\n\n// Execute the search and retrieve the user object\nSearchResponse searchResponse = connection.SendRequest(searchRequest) as SearchResponse;\nDirectoryAttributeCollection attributes = searchResponse.Entries[0].Attributes;\n\n// Get the parent attribute\nDirectoryAttribute parentAttribute = attributes["parent"];\n\n// Get the DN of the parent object\nstring parentDn = parentAttribute.Values[0].ToString();\n\nConsole.WriteLine(parentDn);