using System;\nusing System.IO;\nusing System.Security.Cryptography;\n\nclass Program\n{\n    static void Main()\n    {\n        // Generate a random 5-letter string\n        string fileName = GenerateRandomString(5);\n\n        // Create a text file with the generated name\n        string filePath = @"C:\" + fileName + ".txt";\n        File.Create(filePath).Dispose(); // Create and close the file\n\n        Console.WriteLine("File created: " + filePath);\n    }\n\n    static string GenerateRandomString(int length)\n    {\n        const string chars = "abcdefghijklmnopqrstuvwxyz";\n        byte[] randomBytes = new byte[length];\n        using (var rng = RandomNumberGenerator.Create())\n        {\n            rng.GetBytes(randomBytes);\n        }\n        char[] randomChars = new char[length];\n        for (int i = 0; i < length; i++)\n        {\n            randomChars[i] = chars[randomBytes[i] % chars.Length];\n        }\n        return new string(randomChars);\n    }\n}