// In your ASPX page, add a PlaceHolder control where you want to inject the HTML\n<asp:PlaceHolder ID="myPlaceHolder" runat="server"></asp:PlaceHolder>\n\n// In your code-behind (aspx.cs file)\nprotected void Page_Load(object sender, EventArgs e)\n{\n    // Generate your HTML content as a string\n    string htmlContent = "<img src='image.jpg' /> <span>Some text</span>";\n\n    // Create a Literal control to hold the HTML content\n    Literal literal = new Literal();\n    literal.Text = htmlContent;\n\n    // Add the Literal control to the PlaceHolder\n    myPlaceHolder.Controls.Add(literal);\n}