using System;\nusing System.IO;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static long lastReadPosition = 0;\n\n    static void Main(string[] args)\n    {\n        var watcher = new FileSystemWatcher();\n        watcher.Path = @"C:\Path\To\Your\File";\n        watcher.Filter = "yourfile.csv";\n        watcher.Changed += Watcher_Changed;\n        watcher.EnableRaisingEvents = true;\n    }\n\n    static void Watcher_Changed(object sender, FileSystemEventArgs e)\n    {\n        using (var stream = new FileStream(e.FullPath, FileMode.Open, FileAccess.Read, FileShare.ReadWrite))\n        {\n            stream.Position = lastReadPosition;\n            using (var reader = new StreamReader(stream))\n            {\n                string line;\n                while ((line = reader.ReadLine()) != null)\n                {\n                    // Insert line into database\n                    using (var connection = new SqlConnection("Your Connection String"))\n                    {\n                        connection.Open();\n                        var command = new SqlCommand("INSERT INTO YourTable (Column1, Column2) VALUES (@Column1, @Column2)", connection);\n                        command.Parameters.AddWithValue("@Column1", line.Split(',')[0]);\n                        command.Parameters.AddWithValue("@Column2", line.Split(',')[1]);\n                        command.ExecuteNonQuery();\n                    }\n                }\n            }\n            lastReadPosition = stream.Position;\n        }\n    }\n}