public class CompanyMapping : ClassMap<Company>\n{\n    public CompanyMapping()\n    {\n        WithTable("Companies");\n\n        Id(x => x.Identity, "Id");\n        Map(x => x.Name);           \n\n        HasMany<Contact>(x => x.Contacts)                                               \n            .Inverse()               \n            .LazyLoad()               \n            .Cascade.All()\n            .KeyColumn("CompanyID") // Update this line\n            .AsList();               \n    }\n}