var conditionalChecks = dbContext.ConditionalProperties\n    .Where(cp => cp.IsChecked == 1 && \n               (cp.Property.SystemName == "eatable" || cp.Property.SystemName == "diy"))\n    .Select(cp => cp.ItemId);\n\nvar conditionalCount = conditionalChecks\n    .GroupBy(itemId => itemId)\n    .Select(g => new { ItemId = g.Key, NumTrue = g.Count() });\n\nvar itemResult = conditionalCount\n    .Join(dbContext.Items, cc => cc.ItemId, i => i.Id, (cc, i) => new { cc, i });\n\nvar finalResult = itemResult\n    .Where(ir => ir.cc.NumTrue == 2)\n    .Select(ir => ir.i);