using Zxing;\n\n// Load the image\nBitmap image = new Bitmap("path/to/image.png");\n\n// Create a new barcode reader\nBarcodeReader reader = new BarcodeReader();\n\n// Set the barcode format to PDF-417\nreader.Options.PossibleFormats = new[] { BarcodeFormat.PDF_417 };\n\n// Decode the barcode\nResult result = reader.Decode(image);\n\n// Get the decoded text\nstring decodedText = result.Text;