using OpenQA.Selenium;\nusing OpenQA.Selenium.Firefox;\n\nclass AjaxPageTests\n{\n    [Test]\n    public void TestAjaxPage()\n    {\n        // Create a new instance of the Firefox driver\n        IWebDriver driver = new FirefoxDriver();\n\n        // Navigate to the AJAX-enabled web page\n        driver.Navigate().GoToUrl("https://example.com/ajax-page");\n\n        // Wait for the AJAX request to complete\n        driver.Manage().Timeouts().ImplicitWait = TimeSpan.FromSeconds(10);\n\n        // Find an element on the page that is updated by the AJAX request\n        IWebElement element = driver.FindElement(By.CssSelector("#updated-element"));\n\n        // Verify the element's text or other properties\n        Assert.AreEqual("Expected text", element.Text);\n\n        // Clean up\n        driver.Quit();\n    }\n}