using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class ConnectionStringEncryptor\n{\n    public static string EncryptConnectionString(string connectionString)\n    {\n        byte[] connectionStringBytes = Encoding.UTF8.GetBytes(connectionString);\n        byte[] encryptedBytes = ProtectedData.Protect(connectionStringBytes, null, DataProtectionScope.CurrentUser);\n        return Convert.ToBase64String(encryptedBytes);\n    }\n\n    public static string DecryptConnectionString(string encryptedConnectionString)\n    {\n        byte[] encryptedBytes = Convert.FromBase64String(encryptedConnectionString);\n        byte[] connectionStringBytes = ProtectedData.Unprotect(encryptedBytes, null, DataProtectionScope.CurrentUser);\n        return Encoding.UTF8.GetString(connectionStringBytes);\n    }\n}