var groupQuery = from table in MyTable.AsEnumerable()\n                 group table by new { \n                     Column1 = table["Column1"], \n                     Column2 = table["Column2"] \n                 } into groupedTable\n\n                 select new\n                 {\n                     x = groupedTable.Key.Column1,\n                     y = groupedTable.Key.Column2,\n                     Count = groupedTable.Count()\n                 };