// Define the Entity class\npublic class Entity\n{\n    public virtual int EntityId { get; set; }\n    public virtual string Name { get; set; }\n    // Add other properties as needed\n}\n\n// Define the EntityVarchar class\npublic class EntityVarchar\n{\n    public virtual int EntityId { get; set; }\n    public virtual string AttributeName { get; set; }\n    public virtual string VarcharValue { get; set; }\n}\n\n// Define the EntityFloat class\npublic class EntityFloat\n{\n    public virtual int EntityId { get; set; }\n    public virtual string AttributeName { get; set; }\n    public virtual float FloatValue { get; set; }\n}\n\n// Create a custom mapping for Entity\npublic class EntityMap : ClassMap<Entity>\n{\n    public EntityMap()\n    {\n        Table("Entity");\n        Id(x => x.EntityId);\n        // Map the Name property to the EntityVarchar table\n        Map(x => x.Name).Column("VarcharValue")\n            .Table("EntityVarchar")\n            .Where("AttributeName = 'Name'");\n        // Add other property mappings as needed\n    }\n}