private void MyObject_PropertyChanged(object sender, PropertyChangedEventArgs e)\n{\n    if (e.PropertyName == "MyProperty")\n    {\n        string errorMessage = MyObject.ValidateMyProperty();\n        if (!string.IsNullOrEmpty(errorMessage))\n        {\n            errorProvider1.SetError(txtMyProperty, errorMessage);\n        }\n        else\n        {\n            errorProvider1.SetError(txtMyProperty, string.Empty);\n        }\n    }\n}