// Example using RabbitMQ .NET client\nusing RabbitMQ.Client;\n\n// Create a connection to the RabbitMQ broker\nvar connection = ConnectionFactory.CreateConnection();\n\n// Create a channel to send messages\nvar channel = connection.CreateModel();\n\n// Declare a queue to send messages to\nchannel.QueueDeclare("my_queue", true, false, false, null);\n\n// Send a message to the queue\nvar body = Encoding.UTF8.GetBytes("Hello, world!");\nchannel.BasicPublish(exchange: "", routingKey: "my_queue", body: body);