RichTextBox richTextBox = new RichTextBox();\nrichTextBox.Text = "This is bold text.  This is italicized text.";\nrichTextBox.Select(10, 4); // select the text "bold"\nrichTextBox.SelectionFont = new Font(richTextBox.Font, FontStyle.Bold);\nrichTextBox.Select(25, 10); // select the text "italicized"\nrichTextBox.SelectionFont = new Font(richTextBox.Font, FontStyle.Italic);\nrichTextBox.ReadOnly = true; // make it read-only like a Label\nrichTextBox.BorderStyle = BorderStyle.None; // remove the border\nrichTextBox.BackColor = System.Drawing.Color.Transparent; // make the background transparent