using System;\nusing System.Web.UI;\n\npublic class AccessibleAjaxButton : WebControl\n{\n    protected override void Render(HtmlTextWriter writer)\n    {\n        writer.AddAttribute("type", "button");\n        writer.AddAttribute("aria-label", "Click me");\n        writer.AddAttribute("onclick", "return ajaxClick();");\n        writer.RenderBeginTag("button");\n        writer.Write("Click me");\n        writer.RenderEndTag();\n    }\n\n    protected override void OnPreRender(EventArgs e)\n    {\n        base.OnPreRender(e);\n        ScriptManager.RegisterStartupScript(this, GetType(), "ajaxClick", "function ajaxClick() { /* AJAX code here */ }", true);\n    }\n}