public class MyDbContext : DbContext\n{\n    public DbSet<MyBusinessObject> MyBusinessObjects { get; set; }\n\n    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)\n    {\n        optionsBuilder.UseSqlServer(@"Server=myserver;Database=mydatabase;User Id=myuser;Password=mypassword;");\n    }\n}\n\npublic class MyBusinessObject\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\npublic class DataAccessLayer\n{\n    private readonly MyDbContext _context;\n\n    public DataAccessLayer(MyDbContext context)\n    {\n        _context = context;\n    }\n\n    public IEnumerable<MyBusinessObject> GetMyBusinessObjects()\n    {\n        return _context.MyBusinessObjects.ToList();\n    }\n}