public class CustomSiteMapProvider : XmlSiteMapProvider\n{\n    public override SiteMapNode FindSiteMapNode(string rawUrl)\n    {\n        // Call the base implementation to find the node\n        SiteMapNode node = base.FindSiteMapNode(rawUrl);\n\n        // If the node is found, check if it has multiple parents\n        if (node != null && node.ParentNode != null)\n        {\n            // Get the node's URL\n            string url = node.Url;\n\n            // Get all nodes with the same URL\n            SiteMapNodeCollection nodes = GetNodes(url);\n\n            // If multiple nodes are found, return the first one\n            if (nodes.Count > 0)\n            {\n                return nodes[0];\n            }\n        }\n\n        return node;\n    }\n\n    private SiteMapNodeCollection GetNodes(string url)\n    {\n        SiteMapNodeCollection nodes = new SiteMapNodeCollection();\n\n        // Iterate through all nodes in the site map\n        foreach (SiteMapNode node in GetRootNode().GetAllNodes())\n        {\n            if (node.Url == url)\n            {\n                nodes.Add(node);\n            }\n        }\n\n        return nodes;\n    }\n}