using System;\nusing System.Management;\n\nclass GetWindowsLogonUser\n{\n    static void Main(string[] args)\n    {\n        string machineName = "YourMachineName"; // replace with the machine name\n\n        ManagementScope scope = new ManagementScope("\\\\" + machineName + "\\root\\cimv2");\n        ObjectQuery query = new ObjectQuery("SELECT UserName FROM Win32_ComputerSystem");\n\n        ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query);\n        ManagementObjectCollection results = searcher.Get();\n\n        foreach (ManagementObject result in results)\n        {\n            Console.WriteLine("Windows Logon User: " + result["UserName"].ToString());\n        }\n    }\n}