using System.Windows.Markup;\nusing System.IO;\n\nstring html = "<p>Hello World!</p>";\n\n// Create a StringWriter to hold the XAML output\nStringWriter xamlWriter = new StringWriter();\n\n// Create a XamlWriter to write the XAML\nXamlWriter xamlWriter = new XamlWriter(xamlWriter);\n\n// Parse the HTML and write it to the XAML writer\nHtmlDocument htmlDoc = new HtmlDocument();\nhtmlDoc.LoadHtml(html);\nxamlWriter.WriteNode(htmlDoc.DocumentNode);\n\n// Get the XAML string\nstring xaml = xamlWriter.ToString();