using System.Xml;\n\n// Create an XmlDocument instance\nvar xmlDoc = new XmlDocument();\n\n// Load the XML string into the document\nxmlDoc.LoadXml(samlResponseXml);\n\n// Create an XmlNamespaceManager instance\nvar nsmgr = new XmlNamespaceManager(xmlDoc.NameTable);\nnsmgr.AddNamespace("saml", "urn:oasis:names:tc:SAML:2.0:assertion");\nnsmgr.AddNamespace("samlp", "urn:oasis:names:tc:SAML:2.0:protocol");\n\n// Navigate the XML document using XPath expressions\nvar assertionNode = xmlDoc.SelectSingleNode("//saml:Assertion", nsmgr);