int width = 800; // adjust to your desired width\nint height = 200; // adjust to your desired height\n\n// assume 'g' is your Graphics object\nusing (Pen pen = new Pen(Color.Black))\n{\n    float scaleX = (float)width / L;\n    float scaleY = (float)height / (2 * 32767); // scale to fit the data range\n\n    for (int i = 0; i < L - 1; i++)\n    {\n        float x1 = i * scaleX;\n        float y1 = height / 2 - (float)left[i] * scaleY;\n        float x2 = (i + 1) * scaleX;\n        float y2 = height / 2 - (float)left[i + 1] * scaleY;\n\n        g.DrawLine(pen, x1, y1, x2, y2);\n    }\n}