using System;\nusing System.Threading;\nusing System.Web;\n\npublic class ThreadMonitor\n{\n    private static readonly Dictionary<Guid, Thread> _threads = new Dictionary<Guid, Thread>();\n\n    public static Guid StartThread(Action action)\n    {\n        Guid threadId = Guid.NewGuid();\n        Thread thread = new Thread(new ThreadStart(action));\n        thread.Start();\n        _threads[threadId] = thread;\n        return threadId;\n    }\n\n    public static bool IsThreadRunning(Guid threadId)\n    {\n        if (_threads.TryGetValue(threadId, out Thread thread))\n        {\n            return thread.IsAlive;\n        }\n        return false;\n    }\n}