public void Main()\n{\n    // Get the parameters\n    int param1 = (int)Dts.Variables["param1"].Value;\n    int param2 = (int)Dts.Variables["param2"].Value;\n\n    // Build the SQL command\n    string sqlCommand = string.Format("UPDATE myTable SET columnA = ({0} + {1}), columnB = ({1}) WHERE columnC = ({0} * {1})", param1, param2);\n\n    // Assign the SQL command to a variable\n    Dts.Variables["sqlCommand"].Value = sqlCommand;\n\n    Dts.TaskResult = (int)ScriptResults.Success;\n}