public class WirelessNetworkViewItem : ListViewItem\n{\n    public WirelessNetworkViewItem(WirelessNetwork network)\n    {\n        this.Text = network.Name;\n\n        // Create a panel to hold the network details\n        Panel panel = new Panel();\n        panel.BackColor = Color.White;\n        panel.BorderStyle = BorderStyle.FixedSingle;\n        panel.Width = 200;\n        panel.Height = 50;\n\n        // Add network details to the panel\n        Label label = new Label();\n        label.Text = network.SSID;\n        panel.Controls.Add(label);\n\n        // Add the panel to the ListViewItem\n        this.SubItems.Add(new ListViewSubItem(this, panel));\n    }\n}