using System;\nusing Phalanger;\n\nclass PhpExample {\n    public static void Main(string[] args) {\n        // Create a new PHP runtime\n        var runtime = new ScriptRuntime();\n\n        // Execute a PHP script\n        var phpCode = "<?php echo 'Hello, world!'; ?>";\n        var result = runtime.Execute(phpCode);\n\n        // Print the result\n        Console.WriteLine(result);\n    }\n}