using System;\nusing System.CodeDom;\nusing System.Data.Linq;\nusing System.Data.Linq.Mapping;\n\npublic class CustomCodeGenerator : SqlMetalCodeGenerator\n{\n    protected override void GenerateProperty(Type type, PropertyInfo property, CodeTypeDeclaration decl)\n    {\n        var attribute = new UpdateCheckAttribute(UpdateCheck.WhenChanged);\n        decl.CustomAttributes.Add(new CodeAttributeDeclaration(typeof(UpdateCheckAttribute), new[] { new CodePrimitiveExpression(attribute.UpdateCheck) }));\n        base.GenerateProperty(type, property, decl);\n    }\n}