using System.Drawing.Printing;\n\n// Create a new PrintDocument instance\nPrintDocument printDocument = new PrintDocument();\n\n// Set the printer name to the PDF printer driver\nprintDocument.PrinterSettings.PrinterName = "Adobe PDF";\n\n// Set the output file name\nprintDocument.PrintToFile = true;\nprintDocument.PrintFileName = "output.pdf";\n\n// Print the document\nprintDocument.Print();