using MailKit.Net.Smtp;\nusing MailKit.Security;\nusing MimeKit;\n\n// ...\n\nvar message = new MimeMessage();\nmessage.From.Add(new MailboxAddress("info@eatrightamerica.com"));\nmessage.To.Add(new MailboxAddress(emailParam));\nmessage.Bcc.Add(new MailboxAddress("NPClients@eatrightamerica.com"));\nmessage.Subject = "Your Nutrition Prescription";\nmessage.Body = new TextPart("plain") { Text = msgTxt };\n\nusing (var client = new SmtpClient())\n{\n    client.Connect("smtp.gmail.com", 587, SecureSocketOptions.StartTls);\n    client.Authenticate("your-email@gmail.com", "your-password");\n    client.Send(message);\n    client.Disconnect(true);\n}